css:
  @media screen {
    .print-format {
      padding: 10px !important
    }
  }
    @media print {
    .print {
        display: none;
    }
  }
  @page {
      size: A4 portait;
      margin: 5px;
  }
  .page {
  page-break-after: always;
} 
  .justify-content{
      display: flex;
      flex-direction: column;
      align-items: center; /* Centers content inside the column */
  }
  .w-50{
      width:50%;
  }
  .cs-flex {
      display: flex;
      justify-content: space-between;
  }


  /* Row */
  .cs-row {
    display: flex;
    flex-wrap: wrap;
  }

  /* Columns */
  [class*="cs-col-"] {
    flex-basis: 0;
    flex-grow: 1;
    max-width: 100%;
  }

  /* Column sizes */
  .cs-col-1 {
    flex: 0 0 8.33%;
    max-width: 8.33%;
  }

  .cs-col-2 {
    flex: 0 0 16.66%;
    max-width: 16.66%;
  }

  .cs-col-3 {
    flex: 0 0 25%;
    max-width: 25%;
  }

  .cs-col-4 {
    flex: 0 0 33.33%;
    max-width: 33.33%;
  }

  .cs-col-5 {
    flex: 0 0 41.66%;
    max-width: 41.66%;
  }

  .cs-col-6 {
    flex: 0 0 50%;
    max-width: 50%;
  }

  .cs-col-7 {
    flex: 0 0 58.33%;
    max-width: 58.33%;
  }

  .cs-col-8 {
    flex: 0 0 66.66%;
    max-width: 66.66%;
  }

  .cs-col-9 {
    flex: 0 0 75%;
    max-width: 75%;
  }

  .cs-col-10 {
    flex: 0 0 83.33%;
    max-width: 83.33%;
  }

  .cs-col-11 {
    flex: 0 0 91.66%;
    max-width: 91.66%;
  }

  .cs-col-12 {
    flex: 0 0 100%;
    max-width: 100%;
  }

html:
{% set qty = (doc.qty| default(0) | float) / (doc.custom_unit__pack| default(0) | float) %}
<div class="text-right px-10 mx-1 print">
  {{(qty|adjust_number)}}
</div>
{%  for i in range(1)  %}
    <div class="cs-row">
    {% for item in [1,2,3,4,5,6,7,8] %}
        {% set customer_name = doc.custom_customer_on_label %}
        {% set number = doc.custom_label_no %}
        {% set item_name = doc.item_name %}
        {% set customer_part_no = doc.custom_customer_part_no %}
        {% set barcode_data_1 = (doc.custom_customer_part_no | default('None') | string) %}
        {% set barcode_data_2 = (doc.custom_unit__pack | default('None') | string) ~ ' | ' ~  (doc.custom_lot_no | default('None') | string)  %}
        {% set quantity = doc.custom_unit__pack %}
        {% set lot_no = doc.custom_lot_no %}
        {% set run_card_no = '' %}
        {% set mfg_date = doc.custom_mfg_date | format_date %}
        {% set exp_date = doc.custom_exp_date | format_date %}
        {% set qr_code_data = item_name ~ ' | ' ~ customer_part_no ~ ' | ' ~ quantity ~ ' | ' ~ lot_no ~ ' | ' ~ mfg_date ~ ' | ' ~ exp_date %}
        {% set company_name = 'SIRIPHAT INDUSTRIAL CO., LTD.' %}
        {% set country_of_origin = 'Made in Thailand' %}
        {% set storage_instructions = 'Please keep away from sunlight and store at a temperature not exceeding 40°C' %}

        <div class="cs-col-6 my-1 justify-content">
             {{ frappe.render_template("templates/jinja_lpp_label_2.html", {
                'customer_name': customer_name,
                'number': number,
                'item_name': item_name,
                'customer_part_no': customer_part_no,
                'barcode_data_1': barcode_data_1,
                'barcode_data_2': barcode_data_2,
                'quantity':  quantity ~ ' ' ~ doc.custom_uom,
                'lot_no': lot_no,
                'run_card_no': run_card_no,
                'mfg_date': mfg_date,
                'exp_date': exp_date,
                'qr_code_data': qr_code_data,
                'company_name': company_name,
                'country_of_origin': country_of_origin,
                'storage_instructions': storage_instructions
                }) 
            }}
        </div>
    {% endfor %}
</div>
<div class="page"></div>
{% endfor %}
