css:
@media screen {
  .print-format {
    padding: 10px !important
  }
}
  @media print {
  .print {
      display: none;
  }
}
@page {
    size: A4 portait;
    margin: 10px;
}
.justify-content{
    display: flex;
    flex-direction: column;
    align-items: center; /* Centers content inside the column */
}
.w-50{
    width:50%;
}
.cs-flex {
    display: flex;
    justify-content: space-between;
}


/* Row */
.cs-row {
  display: flex;
  flex-wrap: wrap;
}

/* Columns */
[class*="cs-col-"] {
  flex-basis: 0;
  flex-grow: 1;
  max-width: 100%;
}

/* Column sizes */
.cs-col-1 {
  flex: 0 0 8.33%;
  max-width: 8.33%;
}

.cs-col-2 {
  flex: 0 0 16.66%;
  max-width: 16.66%;
}

.cs-col-3 {
  flex: 0 0 25%;
  max-width: 25%;
}

.cs-col-4 {
  flex: 0 0 33.33%;
  max-width: 33.33%;
}

.cs-col-5 {
  flex: 0 0 41.66%;
  max-width: 41.66%;
}

.cs-col-6 {
  flex: 0 0 50%;
  max-width: 50%;
}

.cs-col-7 {
  flex: 0 0 58.33%;
  max-width: 58.33%;
}

.cs-col-8 {
  flex: 0 0 66.66%;
  max-width: 66.66%;
}

.cs-col-9 {
  flex: 0 0 75%;
  max-width: 75%;
}

.cs-col-10 {
  flex: 0 0 83.33%;
  max-width: 83.33%;
}

.cs-col-11 {
  flex: 0 0 91.66%;
  max-width: 91.66%;
}

.cs-col-12 {
  flex: 0 0 100%;
  max-width: 100%;
}

html:
{% set qty = (doc.qty| default(0) | float) / (doc.custom_unit__box | convert_none_or_zero | float) %}

{% set row_per_page = 3 %}
{% set items = [1]*3 %}
<div class="text-right px-10 mx-1 print">
  {{qty|adjust_number}}
</div>
<div>
    {% for item in items %}
        <div class="my-3 justify-content">
             {{ frappe.render_template("templates/jinja_lpp_label_3.html", {
                'customer_name': doc.custom_customer_on_label | replace_none,
                'number': '',
                'item_name': doc.item_name | replace_none ,
                'customer_part_no': doc.custom_customer_part_no | replace_none,
                'barcode_data_1': doc.custom_customer_part_no | replace_none | str,
                'barcode_data_2': (doc.custom_unit__box| replace_none |str),
                'barcode_data_3': (doc.custom_lot_no| replace_none |str),
                'quantity': doc.custom_unit__box | replace_none,
                'unit' : doc.custom_uom | replace_none,
                'lot_no': doc.custom_lot_no | replace_none,
                'run_card_no': '',
                'mfg_date': doc.custom_mfg_date | replace_none ,
                'exp_date': doc.custom_exp_date | replace_none ,
                'qr_code_data': '',
                'company_name': 'LAMPHUN PLASTPACK CO., LTD.',
                'country_of_origin': 'Made in Thailand',
                'storage_instructions': 'Please keep away from sunlight and store at a temperature not exceeding 40°C'
                }) 
            }}
        </div> 
    {% endfor %}
</div>
