css:
@media screen {
  .print-format {
    padding: 10px !important
  }
}
@page {
    size: A4 portait;
    margin: 10px;
}
.justify-content{
    display: flex;
    flex-direction: column;
    align-items: center; /* Centers content inside the column */
}
.w-50{
    width:50%;
}
.cs-flex {
    display: flex;
    justify-content: space-between;
}


/* Row */
.cs-row {
  display: flex;
  flex-wrap: wrap;
}

/* Columns */
[class*="cs-col-"] {
  flex-basis: 0;
  flex-grow: 1;
  max-width: 100%;
}

/* Column sizes */
.cs-col-1 {
  flex: 0 0 8.33%;
  max-width: 8.33%;
}

.cs-col-2 {
  flex: 0 0 16.66%;
  max-width: 16.66%;
}

.cs-col-3 {
  flex: 0 0 25%;
  max-width: 25%;
}

.cs-col-4 {
  flex: 0 0 33.33%;
  max-width: 33.33%;
}

.cs-col-5 {
  flex: 0 0 41.66%;
  max-width: 41.66%;
}

.cs-col-6 {
  flex: 0 0 50%;
  max-width: 50%;
}

.cs-col-7 {
  flex: 0 0 58.33%;
  max-width: 58.33%;
}

.cs-col-8 {
  flex: 0 0 66.66%;
  max-width: 66.66%;
}

.cs-col-9 {
  flex: 0 0 75%;
  max-width: 75%;
}

.cs-col-10 {
  flex: 0 0 83.33%;
  max-width: 83.33%;
}

.cs-col-11 {
  flex: 0 0 91.66%;
  max-width: 91.66%;
}

.cs-col-12 {
  flex: 0 0 100%;
  max-width: 100%;
}
