css:
.page {
  page-break-after: always;
}

@media screen {
  .print-format {
    padding: 10px !important
  }
}


@page {
    size: A4 portait;
    margin: 10px;
}

.justify-content{
    display: flex;
    flex-direction: column;
    align-items: center; /* Centers content inside the column */
}
.w-50{
    width:50%;
}
.cs-flex {
    display: flex;
    justify-content: space-between;
}

.justify-content-center {
  display: flex;
  justify-content: center;
}

.label {
    width: 10cm;
    height: 2.7cm;
    border: 1px solid #000;
    padding: 10px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    font-size: 12px;
}

.font-thin {
    font-weight: lighter;
    color: rgb(115 115 115);
}

