css:
.page {
  page-break-after: always;
}

table td {
  vertical-align: top !important;
}
.value-text {
    flex: 1;
    text-align: right;
}

.flex-container {
    display: flex;
    justify-content: space-between;
    margin: 0;
}
.master-table table {
  width: 100% !important;
}
 .w-b {
        display: inline-block;
        width: 100px;
    }
.master-table th,
.master-table td {
  padding: 5px !important;
  text-align: left;
  color: black;
}

.print-format td,
.print-format th {
  padding: 5px !important;
}

.master-table .summary td {
  padding: 2px 8px !important;
  text-align: left;
}

/* Row */
.b-row {
  display: flex;
  flex-wrap: wrap;
}

/* Columns */
[class*="b-col-"] {
  flex-basis: 0;
  flex-grow: 1;
  max-width: 100%;
}

/* Column sizes */
.b-col-1 {
  flex: 0 0 8.33%;
  max-width: 8.33%;
}

.b-col-2 {
  flex: 0 0 16.66%;
  max-width: 16.66%;
}

.b-col-3 {
  flex: 0 0 25%;
  max-width: 25%;
}

.b-col-4 {
  flex: 0 0 33.33%;
  max-width: 33.33%;
}

.b-col-5 {
  flex: 0 0 41.66%;
  max-width: 41.66%;
}

.b-col-6 {
  flex: 0 0 50%;
  max-width: 50%;
}

.b-col-7 {
  flex: 0 0 58.33%;
  max-width: 58.33%;
}

.b-col-8 {
  flex: 0 0 66.66%;
  max-width: 66.66%;
}

.b-col-9 {
  flex: 0 0 75%;
  max-width: 75%;
}

.b-col-10 {
  flex: 0 0 83.33%;
  max-width: 83.33%;
}

.b-col-11 {
  flex: 0 0 91.66%;
  max-width: 91.66%;
}

.b-col-12 {
  flex: 0 0 100%;
  max-width: 100%;
}

/* Padding Utilities */
.b-p-0 {
  padding: 0px !important;
}

.b-p-1 {
  padding: 0.25rem;
}

.b-p-2 {
  padding: 0.5rem;
}

.b-p-3 {
  padding: 1rem;
}

.b-p-4 {
  padding: 1.5rem;
}

.b-p-5 {
  padding: 3rem;
}

/* Margin Y-axis Utilities */
.b-my-0 {
  margin-top: 0px !important;
  margin-bottom: 0px !important;
}

.b-my-1 {
  margin-top: 0.25rem;
  margin-bottom: 0.25rem;
}

.b-my-2 {
  margin-top: 0.5rem;
  margin-bottom: 0.5rem;
}

.b-my-3 {
  margin-top: 1rem;
  margin-bottom: 1rem;
}

.b-my-4 {
  margin-top: 1.5rem;
  margin-bottom: 1.5rem;
}

.b-my-5 {
  margin-top: 3rem;
  margin-bottom: 3rem;
}

.fs-detail {
  font-size: 11px;
}

.fs-contact {
  font-size: 11.5px;
}

.print-format .w-100 {
  width: 100%;
}

.print-format .w-50 {
  width: 50%;
}

.add-border {
  border: 1px solid black !important;
}

/* Additional utility classes */
.print-format .text-right {
  text-align: right !important;
}

.print-format .text-center {
  text-align: center !important;
}

.print-format .max-width-logo {
  max-width: 100px !important;
}

.print-format .width-logo {
  width: 200px !important;
}

.print-format .width-address {
  width: auto !important;
}

.print-format .width-tax-id {
  width: 80%;
}

.print-format .width-order-sheet {
  width: auto;
}

.print-format .border-header {
  border: solid 1px;
}

/* styles.css */
.tax-lnvoice-body {
  font-family: Arial, sans-serif;
}

.tax-lnvoice-footer {
  margin-bottom: 20px;
  text-align: center;
}

.tax-lnvoice-signature-section {
  display: flex;
  justify-content: space-between;
}

.tax-lnvoice-signature-box {
  width: 30%;
  text-align: center;
  border-top: 1px solid #000;
  padding-top: 10px;
}

.tax-lnvoice-dotted-line {
  border-bottom: 1px dotted #000;
  width: 100%;
  margin: 1px 0;
}

.tax-lnvoice-solid-line {
  border-bottom: 1px solid #000;
  width: 100%;
  margin: 1px 0;
}
